import React from "react";
import styled from "styled-components";
import Image from "next/image";

const StyledAboutSection = styled.section`
  background: #000;
  margin: 0 auto;
  .inner {
    display: grid;
    grid-template-columns: 3fr 2fr;
    grid-gap: 50px;

    @media (max-width: 768px) {
      display: block;
    }
  }
`;

const StyledText = styled.div`
  ul.skills-list {
    display: grid;
    grid-template-columns: repeat(2, minmax(140px, 200px));
    padding: 0;
    margin: 19px 0 0 0;
    overflow: hidden;
    list-style: none;

    li {
      position: relative;
      margin-bottom: 10px;
      padding-left: 20px;
      font-family: var(--font-mono);
      font-size: var(--fz-xs);
      &:before {
        content: "▹";
        position: absolute;
        left: 0;
        color: var(--green);
        font-size: var(--fz-sm);
        line-height: 12px;
      }
    }
  }
`;

const StyledPic = styled.div`
  position: relative;
  max-width: 300px;
  @media (max-width: 768px) {
    margin: 50px auto 0;
    width: 70%;
  }

  .wrapper {
    display: block;
    position: relative;
    width: 100%;
    border-radius: var(--border-radius);
    background-color: var(--green);
    &:hover,
    &:focus {
      background: transparent;
      outline: 0;
      &:after {
        top: 15px;
        left: 15px;
      }
      .img {
        filter: none;
        mix-blend-mode: normal;
      }
    }

    .img {
      position: relative;
      border-radius: var(--border-radius);
      mix-blend-mode: multiply;
      filter: grayscale(100%) contrast(1);
      transition: var(--transition);
    }

    &:before,
    &:after {
      content: "";
      display: block;
      position: absolute;
      width: 100%;
      height: 100%;
      border-radius: var(--border-radius);
      transition: var(--transition);
    }

    &:before {
      top: 0;
      left: 0;
      background-color: var(--navy);
      mix-blend-mode: screen;
    }

    &:after {
      border: 2px solid var(--green);
      top: 20px;
      left: 20px;
      z-index: -1;
    }
  }
`;

const About = () => {
  const skills = [
    "JavaScript (ES6+)",
    "HTML & (S)CSS",
    "React",
    "Vue",
    "Node.js",
    "WordPress",
  ];

  return (
    <div className="container">
      <div className="container-wrapper">
        <StyledAboutSection>
          <div>
            <h2 className="numbered-heading">About Me</h2>
            <div className="inner">
              <StyledText>
                <div>
                  <p>
                    Hi! There, Ian A. Drilon a student of Iloilo Science and
                    Technology
                  </p>

                  <p>
                    I enjoy creating things that resides in the internet,
                    whether that be websites, applications, or any in between.
                    My goal is always building a scalable, user friendly and
                    pixel-perfect website with performant experience.
                  </p>

                  <p>
                    Here is some technology that i’ve been working recently:
                  </p>
                </div>

                <ul className="skills-list">
                  {skills && skills.map((skill, i) => <li key={i}>{skill}</li>)}
                </ul>
              </StyledText>
              <StyledPic>
                <div className="wrapper">
                  <Image
                    src={"/image/image.png"}
                    alt="Picture of the author"
                    width={500}
                    height={500}
                  />
                </div>
              </StyledPic>
            </div>
          </div>
        </StyledAboutSection>
      </div>
    </div>
  );
};

export default About;
