import React from "react";
import styled from "styled-components";
import Image from "next/image";

const StyledAboutSection = styled.section`
  max-width: 1000px;
  margin: 0 auto;

  .inner {
    display: grid;
    grid-template-columns: 3fr 2fr;
    grid-gap: 50px;

    @media (max-width: 768px) {
      display: block;
    }
  }
`;

const StyledText = styled.div`
  padding: 0px 100px;
  ul.skills-list {
    display: grid;
    grid-template-columns: repeat(2, minmax(140px, 200px));
    padding: 0;
    margin: 19px 0 0 0;
    overflow: hidden;
    list-style: none;

    li {
      position: relative;
      margin-bottom: 10px;
      padding-left: 20px;
      font-family: var(--font-mono);
      font-size: var(--fz-xs);
      &:before {
        content: "â–¹";
        position: absolute;
        left: 0;
        color: var(--green);
        font-size: var(--fz-sm);
        line-height: 12px;
      }
    }
  }
`;

const StyledPic = styled.div`
  position: relative;
  max-width: 300px;
  @media (max-width: 768px) {
    margin: 50px auto 0;
    width: 70%;
  }

  .wrapper {
    display: block;
    position: relative;
    width: 100%;
    border-radius: var(--border-radius);
    background-color: var(--green);
    &:hover,
    &:focus {
      background: transparent;
      outline: 0;
      &:after {
        top: 15px;
        left: 15px;
      }
      .img {
        filter: none;
        mix-blend-mode: normal;
      }
    }

    .img {
      position: relative;
      border-radius: var(--border-radius);
      mix-blend-mode: multiply;
      filter: grayscale(100%) contrast(1);
      transition: var(--transition);
    }

    &:before,
    &:after {
      content: "";
      display: block;
      position: absolute;
      width: 100%;
      height: 100%;
      border-radius: var(--border-radius);
      transition: var(--transition);
    }

    &:before {
      top: 0;
      left: 0;
      background-color: var(--navy);
      mix-blend-mode: screen;
    }

    &:after {
      border: 2px solid var(--green);
      top: 20px;
      left: 20px;
      z-index: -1;
    }
  }
`;

const About = () => {
  const skills = [
    "JavaScript (ES6+)",
    "HTML & (S)CSS",
    "React",
    "Vue",
    "Node.js",
    "WordPress",
  ];

  return (
    <div className="container">
      <div className="container-wrapper">
        <StyledAboutSection>
          <div>
            <h2 className="numbered-heading">About Me</h2>
            <div className="inner">
              <StyledText>
                <div>
                  <p>
                    Hello! I'm Brittany, a software engineer based in Boston,
                    MA.
                  </p>

                  <p>
                    I enjoy creating things that live on the internet, whether
                    that be websites, applications, or anything in between. My
                    goal is to always build products that provide pixel-perfect,
                    performant experiences.
                  </p>

                  <p>
                    Shortly after graduating from{" "}
                    <a href="https://www.ccis.northeastern.edu">
                      Northeastern University
                    </a>
                    , I joined the engineering team at{" "}
                    <a href="https://www.upstatement.com">Upstatement</a> where
                    I work on a wide variety of interesting and meaningful
                    projects on a daily basis.
                  </p>

                  <p>
                    Here are a few technologies I've been working with recently:
                  </p>
                </div>

                <ul className="skills-list">
                  {skills && skills.map((skill, i) => <li key={i}>{skill}</li>)}
                </ul>
              </StyledText>
              <StyledPic>
                <div className="wrapper">
                  <Image
                    src={"/image/image.png"}
                    alt="Picture of the author"
                    width={500}
                    height={500}
                  />
                </div>
              </StyledPic>
            </div>
          </div>
        </StyledAboutSection>
      </div>
    </div>
  );
};

export default About;
